mapper.select:
SELECT box_id, box_num, box_location, create_time, update_time, box_type, server_department, province, city, type_name, type_id, counts FROM ( ( SELECT bi.box_id, bi.box_num, bi.box_location, bi.create_time, bi.update_time, bi.box_type, bi.server_department, bt.province, bt.city, bt.type_name, bi.type_id FROM box_info bi LEFT JOIN box_type bt ON bi.type_id = bt.id
and bi.box_num = #{boxNum, jdbcType=VARCHAR}
and bi.box_id = #{boxId, jdbcType=VARCHAR}
and bi.type_id = #{boxTypeId, jdbcType=VARCHAR}
and bi.create_time <= #{endTime, jdbcType=TIMESTAMP}
and bi.create_time >= #{startTime, jdbcType=TIMESTAMP}
and bi.type_id = #{typeId, jdbcType=VARCHAR}
) AS t LEFT JOIN ( SELECT count(1) AS counts, server_account FROM auth_record GROUP BY server_account HAVING server_account IN ( SELECT DISTINCT (box_num) FROM box_info )
) AS s ON t.box_num = s.server_account ) order by create_time desc
